#include <bits/stdc++.h> using namespace std; void solve(){    int a, b;    cin >> a >> b;    if (a % b == 0)    {        cout << 0 << '\n';        return;    }    int num = a / b;    num = b * (num + 1);    cout << abs(num - a) << '\n';} int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);    cout.tie(nullptr);     int t;    cin >> t;    while (t--)    {        solve();    }     return 0;}