#include <bits/stdc++.h> using namespace std; void solve(){    long long n;    cin >> n;    long long arr[n];    for (long long i = 0; i < n; i++)    {        long long val;        cin >> val;        arr[i] = val;    }    if (n == 1)    {        cout << ((arr[0] > 1) ? "NO" : "YES") << '\n';        return;    }    sort(arr, arr + n);    if (abs(arr[n - 1] - arr[n - 2]) <= 1)    {        cout << "YES" << '\n';    }    else    {        cout << "NO" << '\n';    }} int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);    cout.tie(nullptr);     int t;    cin >> t;    while (t--)    {        solve();    }     return 0;}