#include <bits/stdc++.h> using namespace std; typedef long long ll; void solve(){    ll n;    cin >> n;    if (n == 1)    {        cout << 1 << '\n';        return;    }    ll i = 1;    while (n % i == 0)    {        i++;    }    cout << (i - 1) << '\n';} int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);    cout.tie(nullptr);     int t;    cin >> t;    while (t--)    {        solve();    }     return 0;}